import React, { useState, useEffect } from 'react';
import css from "./ProductDetail.css";
import axios from "axios";
import { Button } from 'react-bootstrap';
import { MdAddShoppingCart } from "react-icons/md";

const ProductDetail = (props) => {
    const { match: { params: { id } } } = props;
    const [product, setProduct] = useState({});

    console.log("id", id);

    useEffect(() => {
        getProductByID();
    }, []);

    const getProductByID = () => {
        axios
            .get(`https://fakestoreapi.com/products/${id}`)
            .then(res => setProduct(res.data))
    }

    return (
        <div className="flex__container">
            <div id="product__img__container">
                <img src={product.image}
                    alt={product.title}
                    className="product__detail__img" />
            </div>

            <div id="product__desc__container">
                <p>{product.title}</p>
                <h3 id="category">{product.category}</h3>

                <p>{product.description}</p>

                <span>Price per unit</span>
                <h4 id="price">${product.price}</h4>

                <Button variant="light" className="btn-sm" id="login__btn">Buy Now</Button>

                <MdAddShoppingCart />

            </div>
        </div>
    )
}

export default ProductDetail
